import React from "react";
import {
  Input,
  Button,
  FormLabel,
  VStack,
  FormControl,
  useDisclosure,
  Modal,
  ModalOverlay,
  ModalHeader,
  ModalFooter,
  ModalContent,
  ModalBody,
  Center,
} from "@chakra-ui/react";
import { Link } from "react-router-dom";
import { Field, ErrorMessage, Formik, Form } from "formik";
import Axios from 'axios';

export const Register = () => {
  const { isOpen, onOpen, onClose } = useDisclosure();

  const onRegister = async (data) => {
    try {
      const response = await Axios.post(`http://localhost:2000/user/register`,data)       
      console.log(response);
    } catch (err) {
      console.log(err);
      alert(err);
    }
  };

  return (
    <>
      <Link onClick={onOpen} style={{ color: "blue" }}>
        Daftar
      </Link>
      <Modal isOpen={isOpen} onClose={onClose}>
        <ModalOverlay />
        <ModalContent>
          <ModalHeader>
            <Center>
              <Link>
                <img
                  alt="svgImg"
                  src="data:image/svg+xml;base64,"
                />
              </Link>
            </Center>
            <Center>Bergabung dengan Twitter sekarang</Center>
          </ModalHeader>
          <ModalBody pb={6}>
            <Formik
              initialValues={{
                password: "",
                username: "",
                email: "",
                password_confirmation: "",
                phone_number: "",
              }}
              onSubmit={(values, action) => {
                onRegister(values);
                action.resetForm();
              }}
            >
              {(props) => {
                return (
                  <>
                    <Form>
                      <VStack spacing={4} align="flex-start">
                        <FormControl>
                          <FormLabel htmlFor="username">Username</FormLabel>
                          <Field
                            as={Input}
                            type="text"
                            name="username"
                            variant="filled"
                          />
                        </FormControl>
                        <FormControl>
                          <FormLabel htmlFor="email">Email</FormLabel>
                          <Field
                            as={Input}
                            type="email"
                            name="email"
                            variant="filled"
                          />
                        </FormControl>
                        <FormControl>
                          <FormLabel htmlFor="username">Phone Number</FormLabel>
                          <Field
                            as={Input}
                            type="text"
                            name="phone_number"
                            variant="filled"
                          />
                        </FormControl>
                        <FormControl>
                          <FormLabel htmlFor="password">Password</FormLabel>
                          <Field
                            as={Input}
                            type="password"
                            name="password"
                            variant="filled"
                          />
                          <ErrorMessage
                            component="div"
                            name="password"
                            style={{ color: "red" }}
                          />
                        </FormControl>
                        <FormControl>
                          <FormLabel>Konfirmasi Password</FormLabel>
                          <Field
                            as={Input}
                            type="password"
                            name="password_confirmation"
                            variant="filled"
                          />
                        </FormControl>
                        <ModalFooter>
                          <Button type="submit" colorScheme="blue" mr={3}>
                            Daftar
                          </Button>
                          <Button onClick={onClose}>Cancel</Button>
                        </ModalFooter>
                      </VStack>
                    </Form>
                  </>
                );
              }}
            </Formik>
          </ModalBody>
        </ModalContent>
      </Modal>
    </>
  );
};
